

# Memoization
def commonChildMemo(s1, s2, m, n, memo):
    
    if m == 0 or n == 0:
        return 0
    
    if memo[m][n] != -1:
        return memo[m][n]
    
    if s1[m-1] == s2[n-1]:
        memo[m][n] = 1 + commonChildMemo(s1, s2, m-1, n-1, memo)
        return memo[m][n]
    
    memo[m][n] = max(commonChildMemo(s1, s2, m, n-1, memo), commonChildMemo(s1, s2, m-1, n, memo))
    return memo[m][n]


# Tabulation
def commonChildTab(s1, s2):

    if len(s1) != len(s2):
        return -1
    
    m = len(s1)
    n = len(s2)
    # max_str = 0    
    table = [[0 for j in range(n+1)] for i in range(m+1)]

    for i in range(1, len(table)):
        for j in range(1, len(table[i])):
            if s1[i-1] == s2[j-1]:
                table[i][j] = table[i-1][j-1] + 1
            else:
                table[i][j] = max(table[i][j-1], table[i-1][j])
    
    return table[m][n]

s1 = ""
s2 = ""


print(commonChildTab(s1, s2))

m = len(s1)
n = len(s2)
memo = [[-1 for i in range(n+1)] for j in range(m+1)]
print(commonChildMemo(s1, s2, m, n, memo))